// Time to units: first 60 min = 2u/15min, then 3u/15min
function tUnits(m) {
  if (m <= 0) return 0;
  if (m <= 60) return Math.ceil(m / 15) * 2;
  return 8 + Math.ceil((m - 60) / 15) * 3;
}
function tDesc(m) {
  if (m <= 0) return "";
  if (m <= 60) { const b = Math.ceil(m/15); return b+"\u00d72 = "+(b*2)+" units"; }
  const e = Math.ceil((m-60)/15);
  return "4\u00d72 + "+e+"\u00d73 = "+(8+e*3)+" units";
}

function consultRate(c, plan) {
  if (plan.id === "classic_ih") return c.ih;
  if (plan.id === "classic_oh") return c.oh;
  return (c.ih / 2.04) * plan.m;
}

function procRate(proc, plan) { return proc.r * BASE_AN * plan.m; }

function modRate(mod, plan) {
  if (mod.t === "an") return mod.u * aU(plan);
  return mod.u * cU(plan);
}

